import React, { useState, useEffect } from "react";
import { addFeedback, getFeedback } from "../api";

const FeedbackForm = ({ eventId, onFeedbackAdded }) => {
  const [feedbackList, setFeedbackList] = useState([]);
  const [form, setForm] = useState({ title: "", description: "" });

  useEffect(() => {
    if (eventId) loadFeedback();
  }, [eventId]);

  const loadFeedback = async () => {
    const res = await getFeedback(eventId);
    setFeedbackList(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.title.trim() || !form.description.trim()) return;
    await addFeedback(eventId, form);
    setForm({ title: "", description: "" });
    await loadFeedback();
    if (onFeedbackAdded) onFeedbackAdded(); // ðŸ”„ trigger summary reload
  };

  return (
    <div className="card">
      <h2>Feedback</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Title"
          value={form.title}
          onChange={(e) => setForm({ ...form, title: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })}
          style={{ width: "100%", height: "80px", resize: "none" }}
        />
        <button type="submit">Submit Feedback</button>
      </form>

      <ul>
        {feedbackList.map((f) => (
          <li key={f.id}>
            <b>{f.title}</b>: {f.description}{" "}
            <span style={{ color: "gray" }}>({f.sentiment})</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FeedbackForm;
